                Translator.translate('Continue shopping (%d second)')
                    .replace('%d', window[delayName])
            );
        }

        if (window[delayName] > 0) {
            window[delayName]--;
        } else {
            AjaxPro.message.hide();
            clearInterval(interval);
        }

